import React,{useEffect,useState} from 'react';
import Header from '../component/Header';
import Footer from '../component/Footer';
import { Form, Button } from "react-bootstrap";
import { AuthContext } from "../component/context";
import { Link } from "react-router-dom";

export default function LoginPage(){

    const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const [msg, setmsg] = useState("");

  const { signIn } = React.useContext(AuthContext);

    function validateForm() {
        return Email.length > 0 && Password.length > 0;
      }

      function handleSubmit(){

      }

    return(
        <div>
      <Header name="Log In" logout={false} />
      <div class='container-fluid'>
        <div className='wlc' class='row'>

        <div className='LogoImg' class='col'>
        <img

              src={require("../component/android-chrome-512x512.png")}
            />
        </div>
      <div className="login" class='col'>
        <Form onSubmit={handleSubmit}>
          <h1>Log In</h1>
          <Form.Group controlId="email" size="lg">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              value={Email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="password" size="lg">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={Password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>

          <Button
            block
            size="lg"
            type="submit"
            // variant="success"
            // isLoading={isLoading}
            disabled={!validateForm()}

          >
            Log In
          </Button>
          {msg}
          <div style={{ textAlign: "right", marginTop: "10px" }}>
            New User ? <Link to="/registration" style={{textDecoration:"none"}}>Registred Here</Link>
          </div>

          <i class="fa-brands fa-google fa-2xl"></i>
          {/* <FontAwesomeIcon icon="fa-brands fa-google fa-2xl" /> */}
        </Form>
      </div>
      </div>
      </div>
      <Footer />
    </div>
    )
}
